INSERT INTO fact VALUES ('linux','Linux is a multiuser UNIX operating system clone originally written by Linus Torvalds. See http://www.linux.com/ for more information.');
INSERT INTO fact VALUES ('lsmod','lsmod lists currently running Linux kernel modules (dynamically loaded device drivers). See also rmmod, insmod, modprobe.');
